import { NextRequest, NextResponse } from "next/server"
import { prisma } from "@/lib/prisma"
import { validateApiKey, hasPermission, corsHeaders } from "@/lib/api-auth"

export async function OPTIONS() {
  return NextResponse.json({}, { headers: corsHeaders() })
}

/**
 * GET /api/external/articles/[slug]
 * Get a single article by slug and optionally increment views
 */
export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ slug: string }> }
) {
  const headers = corsHeaders(request.headers.get("origin") || undefined)
  const { slug } = await params

  // Validate API key
  const validation = await validateApiKey(request)

  if (!validation.isValid) {
    return NextResponse.json(
      { error: validation.error },
      { status: 401, headers }
    )
  }

  if (!hasPermission(validation.apiKey!.permissions, "read")) {
    return NextResponse.json(
      { error: "Missing required permission: read" },
      { status: 403, headers }
    )
  }

  try {
    const { searchParams } = new URL(request.url)
    const incrementViews = searchParams.get("view") === "true"

    // Fetch article
    const article = await prisma.article.findUnique({
      where: { slug },
    })

    if (!article) {
      return NextResponse.json(
        { error: "Article not found" },
        { status: 404, headers }
      )
    }

    // Increment views if requested
    if (incrementViews) {
      await prisma.article.update({
        where: { slug },
        data: { views: { increment: 1 } },
      })
    }

    return NextResponse.json({
      success: true,
      article: {
        id: article.id,
        slug: article.slug,
        title: article.title,
        excerpt: article.excerpt,
        content: article.content,
        coverImage: article.coverImage,
        author: article.author,
        authorAvatar: article.authorAvatar,
        category: article.category,
        tags: article.tags,
        status: article.status,
        publishedAt: article.publishedAt?.toISOString() || null,
        scheduledAt: article.scheduledAt?.toISOString() || null,
        readingTime: article.readingTime,
        views: incrementViews ? article.views + 1 : article.views,
        locale: article.locale,
        seoTitle: article.seoTitle,
        seoDescription: article.seoDescription,
        isAutogenerated: article.isAutogenerated,
        sourceType: article.sourceType,
        createdAt: article.createdAt.toISOString(),
        updatedAt: article.updatedAt.toISOString(),
      },
    }, { headers })
  } catch (error) {
    console.error("Error fetching article:", error)
    return NextResponse.json(
      { error: "Failed to fetch article" },
      { status: 500, headers }
    )
  }
}
